mapper.select:
SELECT * FROM GOODS
 WHERE goods_name like #{search}
 ORDER BY goods_num DESC

SELECT * FROM GOODS
WHERE goods_num like #{search}
ORDER BY goods_num DESC

SELECT * FROM GOODS
WHERE goods_category like #{search}
ORDER BY goods_num DESC

SELECT * FROM GOODS
WHERE goods_best like #{search}
ORDER BY goods_num DESC

mapper.update:
UPDATE GOODS
	SET
		  goods_amount = #{goods_amount}

	WHERE
		goods_num = #{goods_num}

